define([], function () {
  "use strict";

  const autogeneratedCodeStartComment =
    "<!-- autogenerated code, do not edit -->";
  const autogeneratedCodeEndComment = "<!-- end of autogenerated code -->";

  return {
    wrapWithAutogeneratedComment: ($el) => {
      $el.before(autogeneratedCodeStartComment);
      $el.after(autogeneratedCodeEndComment);
    },

    removeAutogeneratedComments: (text) => {
      return text
        .replaceAll(autogeneratedCodeStartComment, "")
        .replaceAll(autogeneratedCodeEndComment, "");
    },
  };
});
